import React, { useState } from "react";
import "../css/inputCss.css";

function CommandLineInterface() {
  const [inputValue, setInputValue] = useState("");
  const [outputValue, setOutputValue] = useState("");

  const handleInputChange = (event) => {
    if (event.target.value[0] === ">") {
      setInputValue(event.target.value);
    } else {
      setInputValue(">" + event.target.value);
    }
  };

  const handleEnterKey = (event) => {
    if (event.key === "Enter") {
      handleCommand(inputValue);
      setInputValue("");
    }
  };

  const handleCommand = (command) => {
    let output = "";

    // Here you can process the command and generate the output accordingly
    switch (command) {
      case "help":
        output = ">Available commands: help, greet, about";
        break;
      case "about":
        output = ">i am a web developer";
        break;
      case "portfolio":
        window.open("https://www.sixfingerhand.xyz/", "_blank");
        output = ">opening url in a new window";
        break;
      case "greet":
        output = ">Hello! How are you?";
        break;
      case "clear":
        break;
      default:
        output = `>Command not found: ${command}`;
        break;
    }

    setOutputValue(
      (prevOutput) => prevOutput + ">" + command + "\n" + output + "\n"
    );
  };

  // Callback function for permanent fixed focus on input
  const onBlur = (e) => {
    e.target.focus();
  };

  return (
    <div>
      <pre>{outputValue}</pre>
      <input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        onKeyDown={handleEnterKey}
        autoFocus
        onBlur={onBlur}
      />
    </div>
  );
}

export default CommandLineInterface;
