import React, { useState } from "react";

function CommandLineInterface() {
  const [inputValue, setInputValue] = useState("");
  const [outputValue, setOutputValue] = useState("");

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleEnterKey = (event) => {
    if (event.key === "Enter") {
      handleCommand(inputValue);
      setInputValue("");
    }
  };

  const handleCommand = (command) => {
    let output = "";

    // Here you can process the command and generate the output accordingly
    switch (command) {
      case "help":
        output = "Available commands: help, greet, about";
        break;
      case "about":
        output = "i am a web developer";
        break;
      case "portfolio":
        output = "i am a web developer";
        break;
      case "greet":
        output = "Hello! How are you?";
        break;
      default:
        output = `Command not found: ${command}`;
        break;
    }

    setOutputValue(
      (prevOutput) => prevOutput + "> " + command + "\n" + output + "\n"
    );
  };

  return (
    <div>
      <pre>{outputValue}</pre>
      <input
        type="text"
        value={inputValue}
        onChange={handleInputChange}
        onKeyDown={handleEnterKey}
        autoFocus
      />
    </div>
  );
}

export default CommandLineInterface;
