// import "@react-pdf-viewer/core/lib/styles/index.css";
import React, { useState, useEffect } from "react";
import Modal from "react-modal";
import { Document, pdfjs } from "react-pdf";
import "../../css/resumePdf.css";

pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@${pdfjs.version}/legacy/build/pdf.worker.min.js`;

const Resume = () => {
  const [modal, setModal] = useState(false);
  //Modal state switcher
  const switchModal = () => {
    setModal(!modal);
  };
  //Modal closes on click outside modal area
  const onOverlayClick = (e) => {
    setModal(false);
    e.stopPropagation();
  };

  useEffect(() => {
    switchModal();
  }, []);

  const handleKeyDown = (e) => {
    if (e.key === "escape") {
      setModal(false);
    }
  };

  document.addEventListener("keydown", handleKeyDown);

  const customStyles = {
    content: {
      height: "90%",
      width: "40%",
      top: "50%",
      left: "50%",
      right: "auto",
      bottom: "auto",
      marginRight: "-50%",
      transform: "translate(-50%, -50%)",
    },
  };
  return (
    <div className="full-screen" onClick={onOverlayClick}>
      <Modal
        isOpen={modal}
        ariaHideApp={false}
        appElement={document.getElementById("app")}
        style={customStyles}
      >
        <div className="container">
          {/* <button onClick={switchModal}>X</button> */}
          {/* <Document file="http://localhost:3000/resume.pdf" /> */}
          <iframe
            className="pdf"
            src="http://localhost:3000/resume.pdf"
          ></iframe>
        </div>
      </Modal>
    </div>
  );
};

export default Resume;
